styles:
.yo-test-component {
  display: block;
  width: 100%;
  height: 200px;
  line-height: 200px;
  text-align: center;
  font-size: 100px;
  color: white;
  background-color: green;
  font-family: 'Source Sans Pro', sans-serif; }

.yo-loader {
  z-index: 1000;
  position: absolute;
  background-color: green;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  color: #fff;
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  -webkit-box-pack: center;
      -ms-flex-pack: center;
          justify-content: center;
  -webkit-box-align: center;
      -ms-flex-align: center;
          align-items: center;
  font-family: "Source Sans Pro", sans-serif; }
  .yo-loader .logo-and-text {
    position: relative;
    text-align: center;
    width: 150px;
    height: 150px; }
    .yo-loader .logo-and-text .loader-text {
      display: inline-block;
      margin-top: 10px;
      font-size: 20px;
      font-weight: 300;
      color: #ffffff; }

@-webkit-keyframes transiteSize {
  0% {
    right: 0;
    bottom: 0;
    opacity: 1; }
  100% {
    right: calc(100% - 90px);
    bottom: calc(100% - 60px);
    opacity: 0; } }

@keyframes transiteSize {
  0% {
    right: 0;
    bottom: 0;
    opacity: 1; }
  100% {
    right: calc(100% - 90px);
    bottom: calc(100% - 60px);
    opacity: 0; } }

@-webkit-keyframes sk-bouncedelay {
  0%, 80%, 100% {
    -webkit-transform: scale(0); }
  40% {
    -webkit-transform: scale(1); } }

@keyframes sk-bouncedelay {
  0%, 80%, 100% {
    -webkit-transform: scale(0);
    transform: scale(0); }
  40% {
    -webkit-transform: scale(1);
    transform: scale(1); } }

.spinner {
  margin: 100px auto 0;
  width: 70px;
  text-align: center; }

.spinner > div {
  width: 18px;
  height: 18px;
  background-color: #fff;
  border-radius: 100%;
  display: inline-block;
  -webkit-animation: sk-bouncedelay 1.4s infinite ease-in-out both;
  animation: sk-bouncedelay 1.4s infinite ease-in-out both; }

.spinner .bounce1 {
  -webkit-animation-delay: -0.32s;
  animation-delay: -0.32s; }

.spinner .bounce2 {
  -webkit-animation-delay: -0.16s;
  animation-delay: -0.16s; }
